function getElementsByXPath(xpath, parent)
{
    let results = [];
    let query = document.evaluate(xpath, parent || document,
        null, XPathResult.ORDERED_NODE_SNAPSHOT_TYPE, null);
    for (let i = 0, length = query.snapshotLength; i < length; ++i) {
        results.push(query.snapshotItem(i));
    }
    return results;
}
function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}
async function downloadAllAssetCsvs(start_idx=0, page_idx=0) {
    try {
        var xpathAssetsDetailsDiv = "//div[text()='View assets details']";
        var xpathDownloadAssetsDetails = "//span[text()='Download']";
        var xpathDownloadCsv = "//span[text()='.csv']";
        var xpathNextPage = "//i[text()='chevron_right']"
        // move to page number page_idx
        for (var idx=0; idx<page_idx; ++idx) {
            getElementsByXPath(xpathNextPage)[3].click()
            await new Promise(r => setTimeout(r, 2000));
        }
        // show 500 ads at once
        // getElementsByXPath("//span[text()='100']")[0].click()
        // await new Promise(r => setTimeout(r, 500));
        // getElementsByXPath("//span[text()='500']")[0].click()
        root_div = document.querySelectorAll('div.base-root')[0]
        root_div.scrollTop = root_div.scrollHeight
        // sleep
        await new Promise(r => setTimeout(r, 500));
        root_div.scrollTop = root_div.scrollHeight
        // sleep
        await new Promise(r => setTimeout(r, 4000));

        // get all assets links
        var t_all_asset_details = getElementsByXPath(xpathAssetsDetailsDiv)

        // go to each ad assets and download csv
        for (var i=start_idx; i<t_all_asset_details.length; ++i) {
            root_div = document.querySelectorAll('div.base-root')[0]
            root_div.scrollTop = root_div.scrollHeight
            // sleep
            await new Promise(r => setTimeout(r, 500));
            root_div.scrollTop = root_div.scrollHeight
            // sleep
            await new Promise(r => setTimeout(r, 4000));
            t_all_asset_details = getElementsByXPath(xpathAssetsDetailsDiv)

            // open ad assets
            console.log(t_all_asset_details[i])
            t_all_asset_details[i].click()

            // sleep
            await new Promise(r => setTimeout(r, 4000));

            // render all assets

            // download csvs
            var t_download_btn = getElementsByXPath(xpathDownloadAssetsDetails)
            console.log(t_download_btn[0])
            t_download_btn[0].click()

            //sleep
            await new Promise(r => setTimeout(r, 1000));

            var t_download_csv = getElementsByXPath(xpathDownloadCsv)
            console.log(t_download_csv[0])
            t_download_csv[0].click()
            console.log(i, '--------------')

            //sleep
            await new Promise(r => setTimeout(r, 15000));

            // close ad assets
            document.querySelectorAll('material-icon.cancel-icon')[0].click()

            // sleep
            await new Promise(r => setTimeout(r, 2000));
            // move to page number page_idx
            for (var idx=0; idx<page_idx; ++idx) {
                getElementsByXPath(xpathNextPage)[3].click()
                await new Promise(r => setTimeout(r, 2000));
            }
        }
        console.log('Download finished for page', page_idx)
    } catch(err) {
        console.log("Error occured")
        console.error(err, err.stack);
        beep()
        await new Promise(r => setTimeout(r, 500));
        beep()
        await new Promise(r => setTimeout(r, 500));
        beep()
    }
}
